import React, { useState, useEffect, useCallback, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Activity, Terminal, Settings, Info, RefreshCw, Zap, 
  Wifi, WifiOff, Search, Globe, FileText, Play, 
  Clock, CheckCircle, XCircle, RotateCcw, MessageSquare,
  Terminal as TerminalIcon, Image, Mic, Calendar, Cpu
} from 'lucide-react';
import GraphComponent from './GraphComponent';
import './App.css';

const POLL_INTERVAL = 3000;
const ACTIVE_THRESHOLD_MS = 60000;

// Tool icon mapping
const TOOL_ICONS = {
  search: Search,
  browser: Globe,
  file: FileText,
  terminal: TerminalIcon,
  image: Image,
  audio: Mic,
  agent: Cpu,
  schedule: Calendar,
  message: MessageSquare,
  weather: Cloud,
  tool: Settings
};

function Cloud(props) {
  return (
    <svg {...props} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M18 10h-1.26A8 8 0 1 0 9 20h9a5 5 0 0 0 0-10z" />
    </svg>
  );
}

const API_BASE = '';

function App() {
  const [graphData, setGraphData] = useState({ nodes: [], links: [], commands: [] });
  const [logs, setLogs] = useState([]);
  const [isConnected, setIsConnected] = useState(false);
  const [lastUpdate, setLastUpdate] = useState(new Date());
  const [showPanel, setShowPanel] = useState(true);
  const [activeAgents, setActiveAgents] = useState(0);
  const [activeTools, setActiveTools] = useState(0);
  const [connectionError, setConnectionError] = useState(null);
  const [selectedCommand, setSelectedCommand] = useState(null);
  const pollCountRef = useRef(0);

  const addLog = useCallback((message, type = 'info') => {
    const newLog = {
      id: Date.now(),
      time: new Date().toLocaleTimeString(),
      message,
      type,
    };
    setLogs(prev => [newLog, ...prev].slice(0, 50));
  }, []);

  const fetchData = useCallback(async () => {
    pollCountRef.current += 1;

    try {
      const response = await fetch(`${API_BASE}/api/sessions`);

      if (!response.ok) throw new Error(`HTTP ${response.status}`);

      const data = await response.json();

      // Count active agents and tools
      const runningAgents = data.nodes.filter(n => 
        (n.type === 'sub-agent' || n.type === 'cron') && n.status === 'running'
      ).length + 1; // +1 for main

      const runningTools = data.nodes.filter(n => 
        n.type === 'tool' && n.status === 'running'
      ).length;

      setGraphData(data);
      setActiveAgents(runningAgents);
      setActiveTools(runningTools);
      setIsConnected(true);
      setConnectionError(null);
      setLastUpdate(new Date());

      const activeSessions = data.nodes
        .filter(n => n.status === 'running' && n.type !== 'main')
        .map(n => n.name)
        .join(', ');

      addLog(`Poll #${pollCountRef.current}: ${runningAgents} agents${activeSessions ? ` (${activeSessions})` : ''}, ${runningTools} tools`, 'info');

    } catch (error) {
      setIsConnected(false);
      setConnectionError(error.message);
      addLog(`Poll #${pollCountRef.current}: ${error.message}`, 'error');
    }
  }, [addLog]);

  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, POLL_INTERVAL);
    return () => clearInterval(interval);
  }, [fetchData]);

  // Format command for display
  const formatCommand = (cmd) => {
    if (cmd.length > 60) return cmd.substring(0, 60) + '...';
    return cmd;
  };

  // Get status color
  const getStatusColor = (status) => {
    switch (status) {
      case 'running': return '#00ffff';
      case 'complete': return '#22c55e';
      case 'aborted': return '#ef4444';
      default: return '#f59e0b';
    }
  };

  return (
    <div className="app-container">
      {/* Header */}
      <header className="header">
        <div className="header-left">
          <Zap className="logo-icon" size={24} />
          <h1 className="title">COSMO COMMAND</h1>
          <span className={`status-badge ${isConnected ? 'online' : 'offline'}`}>
            {isConnected ? 'ONLINE' : 'OFFLINE'}
          </span>
        </div>
        <div className="header-right">
          {isConnected ? <Wifi size={16} className="connection-icon online" /> : <WifiOff size={16} className="connection-icon offline" />}
          <button className="icon-btn" onClick={() => setShowPanel(!showPanel)}>
            <Terminal size={18} />
          </button>
          <button className="icon-btn" onClick={fetchData}>
            <RefreshCw size={18} />
          </button>
        </div>
      </header>

      <div className="main-content">
        {/* Graph Canvas */}
        <div className="graph-container">
          <GraphComponent data={graphData} toolIcons={TOOL_ICONS} />

          {/* Stats Overlay */}
          <div className="stats-overlay">
            <div className="stat-item">
              <Activity size={14} />
              <span className="stat-label">AGENTS</span>
              <span className="stat-value cyan">{activeAgents}</span>
            </div>
            <div className="stat-item">
              <Settings size={14} />
              <span className="stat-label">TOOLS</span>
              <span className="stat-value amber">{activeTools}</span>
            </div>
          </div>

          {!isConnected && (
            <div className="connection-overlay">
              <div className="connection-message">
                <WifiOff size={32} />
                <h3>Bridge Unreachable</h3>
                <p>Start: node session-bridge.js</p>
                <button className="retry-btn" onClick={fetchData}>Retry</button>
              </div>
            </div>
          )}
        </div>

        {/* Side Panel */}
        <AnimatePresence>
          {showPanel && (
            <motion.div
              initial={{ x: 300, opacity: 0 }}
              animate={{ x: 0, opacity: 1 }}
              exit={{ x: 300, opacity: 0 }}
              className="side-panel"
            >
              {/* Command Queue Section */}
              <div className="panel-section">
                <div className="panel-header">
                  <MessageSquare size={16} />
                  <span>COMMAND QUEUE</span>
                  <span className="panel-time">{graphData.commands?.length || 0} total</span>
                </div>

                <div className="commands-container">
                  {graphData.commands?.length === 0 ? (
                    <div className="empty-state">
                      <Info size={20} />
                      <p>No commands yet</p>
                    </div>
                  ) : (
                    graphData.commands.slice(0, 10).map((cmd, idx) => (
                      <motion.div
                        key={idx}
                        initial={{ opacity: 0, x: 20 }}
                        animate={{ opacity: 1, x: 0 }}
                        className={`command-item ${selectedCommand === idx ? 'selected' : ''}`}
                        onClick={() => setSelectedCommand(idx)}
                      >
                        <div className="command-header">
                          <span className="command-text" title={cmd.content}>
                            {formatCommand(cmd.content)}
                          </span>
                          <span className="command-status" style={{ color: getStatusColor(cmd.status) }}>
                            {cmd.status === 'running' ? '●' : cmd.status === 'complete' ? '✓' : '○'}
                          </span>
                        </div>
                        <div className="command-meta">
                          <span className="command-agent">{cmd.sessionName}</span>
                          <span className="command-time">
                            {new Date(cmd.timestamp).toLocaleTimeString()}
                          </span>
                        </div>
                      </motion.div>
                    ))
                  )}
                </div>
              </div>

              {/* System Logs Section */}
              <div className="panel-section logs-section">
                <div className="panel-header">
                  <Terminal size={16} />
                  <span>SYSTEM LOGS</span>
                </div>

                <div className="logs-container">
                  {logs.map(log => (
                    <motion.div
                      key={log.id}
                      initial={{ opacity: 0, y: -10 }}
                      animate={{ opacity: 1, y: 0 }}
                      className="log-entry"
                    >
                      <span className="log-time">{log.time}</span>
                      <span className={`log-type ${log.type}`}>●</span>
                      <span className="log-message">{log.message}</span>
                    </motion.div>
                  ))}
                </div>
              </div>

              <div className="panel-footer">
                <div className="legend">
                  <div className="legend-item"><span className="legend-dot cyan"></span><span>Running</span></div>
                  <div className="legend-item"><span className="legend-dot green"></span><span>Complete</span></div>
                  <div className="legend-item"><span className="legend-dot amber"></span><span>Idle</span></div>
                </div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
}

export default App;
