import React, { useState, useEffect, useCallback, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Activity, Terminal, Settings, Info, RefreshCw, Zap, Wifi, WifiOff } from 'lucide-react';
import GraphComponent from './GraphComponent';
import './App.css';

const POLL_INTERVAL = 3000;
const ACTIVE_THRESHOLD_MS = 60000; // 60 seconds = active, else idle

// API base path - proxied to bridge server
const API_BASE = '';

// Helper to get human-readable name from session
const getSessionName = (session, sessionKey) => {
  // Priority 1: Explicit label
  if (session.label && session.label !== 'unnamed') {
    return session.label;
  }

  // Priority 2: Parse sessionKey for meaningful names
  // Format: agent:main:subagent:UUID or agent:main:cron:UUID
  if (sessionKey && sessionKey.includes(':')) {
    const parts = sessionKey.split(':');
    if (parts.length >= 3) {
      const type = parts[2]; // 'subagent', 'cron', etc.
      const shortId = parts[3] ? parts[3].slice(0, 6) : '';

      // Map types to readable names
      const typeNames = {
        'subagent': 'Sub-Agent',
        'cron': 'Cron Job',
        'main': 'Main',
      };

      const typeName = typeNames[type] || type;
      return shortId ? `${typeName} ${shortId}` : typeName;
    }
  }

  // Priority 3: Agent ID
  if (session.agentId && session.agentId !== 'main') {
    return session.agentId;
  }

  // Fallback: Shortened ID
  const id = session.sessionId || session.id || sessionKey || 'unknown';
  return `Session ${id.slice(0, 6)}`;
};

// Helper to check if session is actively running
const isSessionActive = (session) => {
  // Check explicit status flags
  if (session.status === 'running' || session.status === 'active') return true;
  if (session.status === 'idle' || session.status === 'completed') return false;
  if (session.abortedLastRun) return false;

  // Check timestamp - updatedAt is in milliseconds
  const lastUpdate = session.updatedAt || session.lastMessageAt;
  if (lastUpdate) {
    const ageMs = Date.now() - lastUpdate;
    return ageMs < ACTIVE_THRESHOLD_MS;
  }

  return false;
};

function App() {
  const [graphData, setGraphData] = useState({ nodes: [], links: [] });
  const [logs, setLogs] = useState([]);
  const [isConnected, setIsConnected] = useState(false);
  const [lastUpdate, setLastUpdate] = useState(new Date());
  const [showPanel, setShowPanel] = useState(true);
  const [activeAgents, setActiveAgents] = useState(0);
  const [activeTools, setActiveTools] = useState(0);
  const [connectionError, setConnectionError] = useState(null);
  const pollCountRef = useRef(0);

  // Add log entry helper
  const addLog = useCallback((message, type = 'info') => {
    const newLog = {
      id: Date.now(),
      time: new Date().toLocaleTimeString(),
      message,
      type,
    };
    setLogs(prev => [newLog, ...prev].slice(0, 50));
  }, []);

  // Fetch data from session bridge
  const fetchGatewayData = useCallback(async () => {
    pollCountRef.current += 1;
    const pollNum = pollCountRef.current;

    try {
      const response = await fetch(`${API_BASE}/api/sessions`, {
        method: 'GET',
        headers: { 'Accept': 'application/json' },
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const sessions = await response.json();

      // Transform to graph format
      const nodes = [];
      const links = [];

      // Main agent node
      nodes.push({
        id: 'main',
        name: 'Cosmo',
        type: 'main',
        status: 'running',
      });

      let toolCount = 0;
      let runningAgents = 1;
      const agentNames = [];

      sessions.forEach((session) => {
        const sessionKey = session.sessionKey;
        if (!sessionKey || sessionKey === 'agent:main:main') return;

        const isActive = isSessionActive(session);
        const agentName = getSessionName(session, sessionKey);

        // Track names for logging
        if (isActive) {
          agentNames.push(agentName);
        }

        nodes.push({
          id: sessionKey,
          name: agentName,
          type: sessionKey.includes('cron') ? 'cron' : 'sub-agent',
          status: isActive ? 'running' : 'idle',
        });

        if (isActive) runningAgents++;

        links.push({
          source: 'main',
          target: sessionKey,
          active: isActive,
        });

        // Check for tool calls from sessionFile if accessible
        // For now, simulate based on activity
        if (isActive && session.inputTokens > 0) {
          toolCount++; // Approximate
        }
      });

      setGraphData({ nodes, links });
      setActiveAgents(runningAgents);
      setActiveTools(toolCount);
      setIsConnected(true);
      setConnectionError(null);
      setLastUpdate(new Date());

      // Log EVERY poll cycle with current state
      const statusMsg = agentNames.length > 0 
        ? `Poll #${pollNum}: ${runningAgents} running (${agentNames.join(', ')})`
        : `Poll #${pollNum}: ${runningAgents} agents, idle`;
      addLog(statusMsg, 'info');

    } catch (error) {
      console.error('Bridge connection failed:', error);
      setIsConnected(false);
      setConnectionError(error.message);

      // Mark all as idle on disconnect
      setGraphData(prev => {
        if (prev.nodes.length === 0) {
          return {
            nodes: [{ id: 'main', name: 'Cosmo', type: 'main', status: 'idle' }],
            links: []
          };
        }
        return {
          nodes: prev.nodes.map(n => ({ ...n, status: 'idle' })),
          links: prev.links.map(l => ({ ...l, active: false }))
        };
      });

      addLog(`Poll #${pollNum}: Connection failed - ${error.message}`, 'error');
    }
  }, [addLog]);

  // Poll gateway
  useEffect(() => {
    fetchGatewayData();
    const interval = setInterval(fetchGatewayData, POLL_INTERVAL);
    return () => clearInterval(interval);
  }, [fetchGatewayData]);

  return (
    <div className="app-container">
      {/* Header */}
      <header className="header">
        <div className="header-left">
          <Zap className="logo-icon" size={24} />
          <h1 className="title">COSMO COMMAND</h1>
          <span className={`status-badge ${isConnected ? 'online' : 'offline'}`}>
            {isConnected ? 'ONLINE' : 'OFFLINE'}
          </span>
        </div>
        <div className="header-right">
          <div className="connection-info" title={connectionError || 'Connected'}>
            {isConnected ? (
              <Wifi size={16} className="connection-icon online" />
            ) : (
              <WifiOff size={16} className="connection-icon offline" />
            )}
          </div>
          <button className="icon-btn" onClick={() => setShowPanel(!showPanel)}>
            <Terminal size={18} />
          </button>
          <button className="icon-btn" onClick={fetchGatewayData} title="Refresh">
            <RefreshCw size={18} />
          </button>
        </div>
      </header>

      {/* Main Content */}
      <div className="main-content">
        {/* Graph Canvas */}
        <div className="graph-container">
          <GraphComponent data={graphData} />

          {/* Overlay Stats */}
          <div className="stats-overlay">
            <div className="stat-item">
              <Activity size={14} className="stat-icon" />
              <span className="stat-label">AGENTS</span>
              <span className="stat-value cyan">{activeAgents}</span>
            </div>
            <div className="stat-item">
              <Settings size={14} className="stat-icon" />
              <span className="stat-label">TOOLS</span>
              <span className="stat-value amber">{activeTools}</span>
            </div>
          </div>

          {/* Connection Error Overlay */}
          {!isConnected && (
            <div className="connection-overlay">
              <div className="connection-message">
                <WifiOff size={32} />
                <h3>Bridge Unreachable</h3>
                <p>Start the bridge: node session-bridge.js</p>
                <p className="error-detail">{connectionError}</p>
                <button className="retry-btn" onClick={fetchGatewayData}>
                  Retry Connection
                </button>
              </div>
            </div>
          )}
        </div>

        {/* Side Panel */}
        <AnimatePresence>
          {showPanel && (
            <motion.div
              initial={{ x: 300, opacity: 0 }}
              animate={{ x: 0, opacity: 1 }}
              exit={{ x: 300, opacity: 0 }}
              transition={{ type: 'spring', damping: 25, stiffness: 200 }}
              className="side-panel"
            >
              <div className="panel-header">
                <Terminal size={16} />
                <span>SYSTEM LOGS</span>
                <span className="panel-time">
                  {lastUpdate.toLocaleTimeString()}
                </span>
              </div>

              <div className="logs-container">
                {logs.length === 0 ? (
                  <div className="empty-logs">
                    <Info size={24} className="empty-icon" />
                    <p>Waiting for polls...</p>
                  </div>
                ) : (
                  logs.map(log => (
                    <motion.div
                      key={log.id}
                      initial={{ opacity: 0, y: -10 }}
                      animate={{ opacity: 1, y: 0 }}
                      className="log-entry"
                    >
                      <span className="log-time">{log.time}</span>
                      <span className={`log-type ${log.type}`}>●</span>
                      <span className="log-message">{log.message}</span>
                    </motion.div>
                  ))
                )}
              </div>

              <div className="panel-footer">
                <div className="legend">
                  <div className="legend-item">
                    <span className="legend-dot cyan"></span>
                    <span>Running</span>
                  </div>
                  <div className="legend-item">
                    <span className="legend-dot amber"></span>
                    <span>Idle</span>
                  </div>
                </div>
                <div className="poll-info">
                  Poll: {POLL_INTERVAL/1000}s • #{pollCountRef.current}
                </div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
}

export default App;
